import { Component, OnInit } from '@angular/core';
import { Materie } from '../../data/interfaces/materie';
import { ApiService } from '../../core/services/api.service';
import { CommonModule } from '@angular/common';
import { MatCardModule } from '@angular/material/card';

@Component({
  selector: 'app-get-materii',
  templateUrl: './get-materii.component.html',
  styleUrls: ['./get-materii.component.css'],
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule
  ]
})
export class GetMateriiComponent implements OnInit{
  private readonly path: string = "/examen/materii";

  public materii: Materie[] = [];

  constructor(private readonly apiService: ApiService) { }

  ngOnInit() {
    this.apiService.get(this.path).subscribe(
      (data: any) => {
        this.materii = data;
      },
      (error: any) => {
        console.error(error.error.message);
      });
  }


}
<div *ngFor="let item of materii" class="container">
  <mat-card>{{ item.denumire }}</mat-card>
  <mat-card>{{ item.nrOreSapt }}</mat-card>
</div>
