import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators } from "@angular/forms";
import { ApiService } from '../../core/services/api.service';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-assign-materie',
  templateUrl: './assign-materie.component.html',
  styleUrls: ['./assign-materie.component.css'],
  standalone: true,
  imports: [
    ReactiveFormsModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
  ]
})
export class AssignMaterieComponent {
  private readonly path: string = "/examen/assignMaterii";
  constructor(private readonly formBuilder: FormBuilder,
    private readonly apiService: ApiService) { }

  addMatForm = this.formBuilder.group({
    numeProfesor: ['', Validators.required],
    prenumeProfesor: [''],
    denumireMaterie: ['', Validators.required]
  });

  send() {
    console.log(this.addMatForm.value);

    this.apiService.post(this.path, this.addMatForm.value).subscribe(
      (data: any) => {
        console.log(data)
      },
      (error: any) => {
        console.error(error.error.message);
      });
  }
}
<form class="profForm" [formGroup]="addMatForm">
  <mat-form-field appearance="fill">
    <mat-label>Nume</mat-label>
    <input matInput formControlName="numeProfesor" type="text" placeholder="nume" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Prenume</mat-label>
    <input matInput formControlName="prenumeProfesor" type="text" placeholder="prenume">
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Denumire</mat-label>
    <input matInput formControlName="denumireMaterie" type="text" placeholder="denumire" required>
  </mat-form-field>

  <button mat-raised-button color="primary" (click)="send()">Submit</button>
</form>
