import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators } from "@angular/forms";
import { ApiService } from '../../core/services/api.service';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-add-materie',
  templateUrl: './add-materie.component.html',
  styleUrls: ['./add-materie.component.css'],
  standalone: true,
  imports: [
    ReactiveFormsModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
  ]
})
export class AddMaterieComponent {
  private readonly path: string = "/examen/createMaterie";
  constructor(private readonly formBuilder: FormBuilder,
    private readonly apiService: ApiService) { }

  addMatForm = this.formBuilder.group({
    denumire: ['', Validators.required],
    nrOreSapt: ['', Validators.required]
  });

  send() {
    console.log(this.addMatForm.value);

    this.apiService.post(this.path, this.addMatForm.value).subscribe(
      (data: any) => {
        console.log(data)
      },
      (error: any) => {
        console.error(error.error.message);
      });
  }
}
<form class="profForm" [formGroup]="addMatForm">
  <mat-form-field appearance="fill">
    <mat-label>Denumire</mat-label>
    <input matInput formControlName="denumire" type="text" placeholder="denumire" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Numar de ore pe saptamana</mat-label>
    <input matInput formControlName="nrOreSapt" type="text" placeholder="numar ore pe saptamana" required>
  </mat-form-field>

  <button mat-raised-button color="primary" (click)="send()">Submit</button>
</form>
