import { Component, OnInit } from '@angular/core';
import { Profesor } from '../../data/interfaces/profesor';
import { ApiService } from '../../core/services/api.service';
import { CommonModule } from '@angular/common';
import { MatCardModule } from '@angular/material/card';

@Component({
  selector: 'app-get-profesori',
  templateUrl: './get-profesori.component.html',
  styleUrls: ['./get-profesori.component.css'],
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule
  ]

})
export class GetProfesoriComponent implements OnInit {

  private readonly path: string = "/examen";

  public profesori: Profesor[] = [];
  constructor(private readonly apiService: ApiService) {

  }

  ngOnInit() {
    this.apiService.get(this.path).subscribe(
      (data: any) => {
        this.profesori = data;
      },
      (error: any) => {
        console.error(error.error.message);
      });
  }

}
<div *ngFor="let item of profesori" class="container">
  <mat-card>{{ item.nume }}</mat-card>
  <mat-card>{{ item.prenume }}</mat-card>
  <mat-card *ngIf="item.tipProfesor == 0">standard</mat-card>
  <mat-card *ngIf="item.tipProfesor == 1">laborant</mat-card>
</div>
